    private void btnAceptarActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_btnAceptarActionPerformed
    {//GEN-HEADEREND:event_btnAceptarActionPerformed
        String user = jtfUsuario.getText().toString();
        String pass = jpassContraenya.getText().toString();
        try
        {
            utils.UtilisSql.loginUser(user, pass, this);
        } catch (ClassNotFoundException ex)
        {
            Logger.getLogger(Login.class.getName()).log(Level.SEVERE, null, ex);
        } catch (SQLException ex)
        {
            Logger.getLogger(Login.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_btnAceptarActionPerformed

    private void jpassContraenyaKeyPressed(java.awt.event.KeyEvent evt)//GEN-FIRST:event_jpassContraenyaKeyPressed
    {//GEN-HEADEREND:event_jpassContraenyaKeyPressed
        if (evt.getKeyCode() == KeyEvent.VK_ENTER)
        {
            String user = jtfUsuario.getText().toString();
            String pass = jpassContraenya.getText().toString();
            try
            {
                utils.UtilisSql.loginUser(user, pass, this);
            } catch (ClassNotFoundException ex)
            {
                Logger.getLogger(Login.class.getName()).log(Level.SEVERE, null, ex);
            } catch (SQLException ex)
            {
                Logger.getLogger(Login.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }//GEN-LAST:event_jpassContraenyaKeyPressed

